
function generateID({length = 20, allowedSymbols = "qwertyuiopasdfghjklzxcvbnmQWERTYUIOPASDFGHJKLZXCVBNM0123456789-_", allowSpecChars = true, allowNumbers = true, allowCapitalLetters = true, allowLowercaseLetters = true, min = 0} = {}) {
var generatedID = "";

if(!allowCapitalLetters) {
allowedSymbols = allowedSymbols.replace(/[A-Z]/g, '')
}

if(!allowLowercaseLetters) {
allowedSymbols = allowedSymbols.replace(/[a-z]/g, '')
}



if(!allowSpecChars) {
allowedSymbols = allowedSymbols.replace(/[\~\`\|\•\√\π\÷\×\¶\∆\£\¢\€\¥\^\°\=\{\}\\\%\©\®\™\℅\[\]\@\#\$\_\&\-\+\(\)\/\*\"\'\:\;\!\?\<\>\,\.]/g, '');
}
if(!allowNumbers) {
allowedSymbols = allowedSymbols.replace(/[0-9]/g, '');
}

if(allowedSymbols !== "") {
if(Math.floor(min) > Math.floor(length)) {
length = min;
}
for(var i = 0; i < length; i++) {
generatedID += allowedSymbols[Math.floor(Math.random() * allowedSymbols.length)]
}

return generatedID;
}
else {
return "";
}
}
